import React, { useEffect, useState, useRef } from "react";

const HOUSES = ["Gryff", "Slyth", "Raven", "Huff"];
const API_BASE = "http://localhost:5000";

function formatNum(n) {
  return n.toLocaleString();
}

export default function App() {
  const [windowSel, setWindowSel] = useState("all");
  const [totals, setTotals] = useState({ Gryff:0, Slyth:0, Raven:0, Huff:0 });
  const [live, setLive] = useState(false);
  const esRef = useRef(null);

  async function fetchTotals(win) {
    const r = await fetch(`${API_BASE}/api/totals?window=${win}`);
    const j = await r.json();
    setTotals(j.totals);
  }

  useEffect(() => { fetchTotals(windowSel); }, [windowSel]);

  useEffect(() => {
    if (!live) {
      if (esRef.current) { esRef.current.close(); esRef.current = null; }
      return;
    }
    const es = new EventSource(`${API_BASE}/stream`);
    es.onmessage = () => { fetchTotals(windowSel); };
    es.onerror = (err) => { es.close(); esRef.current = null; };
    esRef.current = es;
    return () => { es.close(); esRef.current = null; };
  }, [live, windowSel]);

  const maxPoints = Math.max(...HOUSES.map(h => totals[h] || 0), 1);

  return (
    <div style={{maxWidth:900, margin:"20px auto", fontFamily:"Arial"}}>
      <h2>🏆 Hogwarts House Cup — Leaderboard</h2>
      <div style={{display:"flex", gap:12, alignItems:"center", marginBottom:12}}>
        <div>
          <button onClick={()=>setWindowSel("5m")} className={windowSel==="5m" ? "sel":""}>Last 5 minutes</button>
          <button onClick={()=>setWindowSel("1h")} className={windowSel==="1h" ? "sel":""}>Last 1 hour</button>
          <button onClick={()=>setWindowSel("all")} className={windowSel==="all" ? "sel":""}>All time</button>
        </div>
        <div style={{marginLeft:"auto"}}>
          <label><input type="checkbox" checked={live} onChange={(e)=>setLive(e.target.checked)} /> Start Updates</label>
        </div>
      </div>
      <div style={{padding:12, border:"1px solid #ddd", borderRadius:8, background:"#fff"}}>
        {HOUSES.map(house => {
          const points = totals[house] || 0;
          const pct = Math.round((points / maxPoints) * 100);
          return (
            <div key={house} style={{display:"flex", alignItems:"center", marginBottom:12}}>
              <div style={{width:90, textAlign:"right", marginRight:12}}>{house}</div>
              <div style={{flex:1, marginRight:12, background:"#f2f4f7", height:36, borderRadius:6, overflow:"hidden"}}>
                <div style={{height:"100%", width:`${pct}%`, paddingLeft:8}}><strong>{formatNum(points)}</strong></div>
              </div>
              <div style={{width:70}}><small>{points}</small></div>
            </div>
          );
        })}
      </div>
    </div>
  );
}
