import React, { useEffect, useState, useRef } from "react";

const HOUSES = [
  { name: "Gryff", icon: "ü¶Å" },
  { name: "Slyth", icon: "üêç" },
  { name: "Raven", icon: "ü¶Ö" },
  { name: "Huff", icon: "ü¶°" },
];

const API_BASE = "http://localhost:5000";

function formatNum(n) {
  return n.toLocaleString();
}

export default function App() {
  const [windowSel, setWindowSel] = useState("all");
  const [totals, setTotals] = useState({ Gryff: 0, Slyth: 0, Raven: 0, Huff: 0 });
  const [live, setLive] = useState(false);
  const esRef = useRef(null);

  async function fetchTotals(win) {
    try {
      const r = await fetch(`${API_BASE}/api/totals?window=${win}`);
      const j = await r.json();
      setTotals(j.totals);
    } catch (err) {
      console.error("Error fetching totals:", err);
    }
  }

  useEffect(() => { fetchTotals(windowSel); }, [windowSel]);

  useEffect(() => {
    if (!live) {
      if (esRef.current) { esRef.current.close(); esRef.current = null; }
      return;
    }
    const es = new EventSource(`${API_BASE}/stream`);
    es.onmessage = () => { fetchTotals(windowSel); };
    es.onerror = () => { es.close(); esRef.current = null; };
    esRef.current = es;
    return () => { es.close(); esRef.current = null; };
  }, [live, windowSel]);

  const maxPoints = Math.max(...HOUSES.map(h => totals[h.name] || 0), 1);

  return (
    <div style={{ maxWidth: 700, margin: "20px auto", fontFamily: "Arial, sans-serif" }}>
      <h3 style={{ textAlign: "center" }}>üìä Live Leaderboard</h3>
      <div style={{ display: "flex", justifyContent: "center", gap: 12, marginBottom: 16 }}>
        <button onClick={() => setLive(!live)}>
          {live ? "‚è∏ Stop Updates" : "‚ñ∂ Start Updates"}
        </button>
        <select value={windowSel} onChange={(e) => setWindowSel(e.target.value)}>
          <option value="5m">Last 5 minutes</option>
          <option value="1h">Last 1 hour</option>
          <option value="all">All Time</option>
        </select>
      </div>

      <div style={{ padding: 16, border: "1px solid #ddd", borderRadius: 8, background: "#fff" }}>
        {HOUSES.map(h => {
          const points = totals[h.name] || 0;
          const pct = Math.round((points / maxPoints) * 100);

          return (
            <div key={h.name} style={{ display: "flex", alignItems: "center", marginBottom: 20 }}>
              {/* Icon + Name */}
              <div style={{ width: 80, display: "flex", alignItems: "center", gap: 6 }}>
                <span style={{ fontSize: "1.2em" }}>{h.icon}</span>
                <span>{h.name}</span>
              </div>

              {/* Progress bar container */}
              <div
                style={{
                  flex: 1,
                  background: "#f2f4f7",
                  height: 32,
                  borderRadius: 6,
                  overflow: "hidden",
                  marginRight: 12,
                  position: "relative",
                  cursor: "pointer",
                }}
              >
                {/* Filled bar */}
                <div
                  style={{
                    height: "100%",
                    width: `${pct}%`,
                    background: "#223fcd",
                    transition: "width 0.5s ease",
                  }}
                  className="progress-bar"
                >
                  {/* Tooltip box */}
                  <div
                    style={{
                      position: "absolute",
                      bottom: "110%",
                      left: "50%",
                      transform: "translateX(-50%)",
                      background: "#333",
                      color: "#fff",
                      padding: "6px 10px",
                      borderRadius: 6,
                      whiteSpace: "nowrap",
                      display: "flex",
                      alignItems: "center",
                      gap: 6,
                      fontSize: 12,
                      pointerEvents: "none",
                      opacity: 0,
                      transition: "opacity 0.2s",
                    }}
                    className="tooltip-box"
                  >
                    <span style={{ fontSize: "1.2em" }}>{h.icon}</span>
                    <span>{h.name}</span>
                    <span>Count: {points}</span>
                  </div>
                </div>
              </div>
            </div>
          );
        })}
      </div>

      {/* Tooltip hover effect */}
      <style>
        {`
          .progress-bar:hover .tooltip-box {
            opacity: 1;
          }
        `}
      </style>
    </div>
  );
}
